    private void equalToBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalToBtnActionPerformed
        // TODO add your handling code here:
        try {
            // Handle addition
            if (txt.contains("+")) {
                int num1 = Integer.parseInt(txt.substring(0, txt.indexOf("+")));
                int num2 = Integer.parseInt(txt.substring(txt.indexOf("+") + 1));
                txt = Integer.toString(num1 + num2);
            }
            // Handle subtraction
            else if (txt.contains("-")) {
                int num1 = Integer.parseInt(txt.substring(0, txt.indexOf("-")));
                int num2 = Integer.parseInt(txt.substring(txt.indexOf("-") + 1));
                txt = Integer.toString(num1 - num2);
            }
            // Handle multiplication
            else if (txt.contains("x")) {
                int num1 = Integer.parseInt(txt.substring(0, txt.indexOf("x")));
                int num2 = Integer.parseInt(txt.substring(txt.indexOf("x") + 1));
                txt = Integer.toString(num1 * num2);
            }
            // Handle division
            else if (txt.contains("/")) {
                int num1 = Integer.parseInt(txt.substring(0, txt.indexOf("/")));
                int num2 = Integer.parseInt(txt.substring(txt.indexOf("/") + 1));
                if (num2 != 0) {
                    txt = Integer.toString(num1 / num2);
                } else {
                    txt = "Can't Divide By Zero!";
                }
            }
            // Handle modulus
            else if (txt.contains("%")) {
                int num1 = Integer.parseInt(txt.substring(0, txt.indexOf("%")));
                int num2 = Integer.parseInt(txt.substring(txt.indexOf("%") + 1));
                if (num2 != 0) {
                    txt = Integer.toString(num1 % num2);
                } else {
                    txt = "Can't Divide By Zero!";
                }
            }

            // Update the result in the text field
            jTextField1.setText(txt);
        } catch (NumberFormatException e) {
            // If input is invalid, show error message in the text field
            txt = "Invalid Input!";
            jTextField1.setText(txt);
        }
    }//GEN-LAST:event_equalToBtnActionPerformed

