    private void appendToTextField(String value) {
    if(txt.equals("Can't Divide By Zero!") || txt.equals("Invalid Input!")) {
        txt = value; // Reset if the error message is present
    } else {
        txt += value; // Append the value to the existing text
    }
    jTextField1.setText(txt); // Update the text field
}

   
